create table curso (id int4 generated by default as identity, instituicao_de_ensino varchar(255), nome varchar(255), periodo int4, sigla int4, primary key (id));
create table curso_disciplina (curso_id int4 not null, disciplina_id int4 not null);
create table disciplina (id int4 generated by default as identity, carga_horaria int4, codigo varchar(255), nome varchar(255), docente_id int4, primary key (id));
create table docente (id int4 generated by default as identity, nome varchar(255), turno int4, primary key (id));
create table grade (id int4 generated by default as identity, hora_criacao timestamp, nome varchar(255), usuario_id int4, primary key (id));
create table grade_disciplina (grade_id int4 not null, disciplina_id int4 not null);
create table grade_docente (grade_id int4 not null, docente_id int4 not null);
create table perfis (usuario_id int4 not null, perfis int4);
create table restricao (id int4 generated by default as identity, dia_da_semana int4, nome varchar(255), docente_id int4, primary key (id));
create table turno (id int4 generated by default as identity, hora_inicio varchar(255), hora_termino varchar(255), nome varchar(255), primary key (id));
create table usuario (id int4 generated by default as identity, email varchar(255), nome varchar(255), senha varchar(255), primary key (id));
